	app.service("ChartService", [ChartService]);
	function ChartService(){
		var service = this;

		service.CreateChart = CreateChart;

		function CreateChart(type, element, scope){
			// https://bost.ocks.org/mike/path/

			var initial_data = false;

			var range = 400;
			var duration = 1000;

			var now = new Date(Date.now() - duration);
			var data = d3.range(range).map(function() { return 0; });

			var margin = {top: 0, right: 0, bottom: 20, left: 50};
			var width = 850;
			var boxWidth = element.find("svg").parent()[0].clientWidth;
			var height = element.find("svg").parent()[0].clientHeight - margin.bottom;

			var x = d3.time.scale()
			    .domain([now * duration, now - duration])
			    .range([0, width]);

			var y = d3.scale.linear()
			    .range([height, 0]);

			var line = d3.svg.line()
			    .interpolate("basis")
			    .x(function(d, i) { return x(now - (range - 1 - i) * duration); })
			    .y(function(d, i) { return y(d); });

			var svg = d3.select(element.find("svg")[0])
			    .attr("width", width)
			    .attr("height", height + margin.top + margin.bottom)
			  	.append("g");

			svg.append("defs").append("clipPath")
			    .attr("id", "clip")
			  	.append("rect")
			    .attr("width", width)
			    .attr("height", height);

			var x_axis = svg.append("g")
			    .attr("class", "x-axis")
			    .attr("transform", "translate(0," + height + ")")
			    .call(x.axis = d3.svg.axis().scale(x).orient("bottom"));

			var path = svg.append("g")
			    .attr("clip-path", "url(#clip)")
			  	.append("path")
			    .datum(data)
			    .attr("class", "line");

			var transition = d3.select({}).transition()
			    .duration(1000)
			    .ease("linear");

			(function tick() {
			  transition = transition.each(function() {

			    // update the domains
			    now = new Date();
			    x.domain([now - (range - 2) * duration, now - duration]);
			    y.domain([0, d3.max(data)]);

			    // To set initial data on sensor charts
			    if(!initial_data){
			    	switch(type){
						case "light-sensor":
							data.push(13000);
							break;
						case "pir-sensor":
							data.push(0);
							break;
						default:
							data.push(0);
							break;
					}
					initial_data = true;
			    } else{
			    	data.push(scope.Data);
			    }

			    // redraw the line
			    svg.select(".line")
			        .attr("d", line)
			        .attr("transform", null);

			    // slide the x-axis left
			    x_axis.call(x.axis);

			    // slide the line left
			    path.transition()
			        .attr("transform", "translate(" + x(now - (range - 1) * duration) + ")");

			    // pop the old data point off the front
			    data.shift();

			  }).transition().each("start", tick);
			})();
		}

		return service;
	}

